sys_script_include.script:
var u_getMembers = Class.create();
u_getMembers.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	u_getMembers: function(groupSysId) {
        var userIds = [];
        var gr = new GlideRecord('sys_user_grmember');
        gr.addQuery('group', groupSysId);
        gr.query();
        while (gr.next()) {
            userIds.push(gr.getValue('user'));
        }
        userIds = userIds.joim(',');
        return "sys_idIN" + userIds;
    },

    type: 'u_getMembers'
});
